  <span class="token property">background</span><span class="token punctuation">:</span> <span class="token function">rgba</span><span class="token punctuation">(</span>204<span class="token punctuation">,</span> 198<span class="token punctuation">,</span> 198<span class="token punctuation">,</span> 0.7<span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token punctuation">}</span>

<span class="token selector">#prev img</span> <span class="token punctuation">{</span>
